Driver::Driver() 
{
   BytesReceived = 0; BytesRecovered = 0; BytesWritten = 0;
   driverstr = "Driver: ";
   imageSize = 5120;
   memset(bufferRx, 0, sizeof bufferRx);
   memset(bufferRecover, 0, sizeof bufferRecover);
}

int Driver::connect(char* serial, uint timeoutms) 
{
   ftStatus  = FT_OpenEx(serial, FT_OPEN_BY_SERIAL_NUMBER, &ftHandle);
   ftStatus |= FT_ResetDevice(ftHandle);
   ftStatus |= FT_SetTimeouts(ftHandle, timeoutms, 10); // r/w
   ftStatus |= FT_SetFlowControl(ftHandle, FT_FLOW_RTS_CTS, 0, 0); // last two not used
   ftStatus |= FT_SetDataCharacteristics(ftHandle, FT_BITS_8, FT_STOP_BITS_1, FT_PARITY_NONE); // IMPORTANT FOR INTEGRITY
   ftStatus |= FT_SetLatencyTimer(ftHandle, 1);
   ftStatus |= FT_Purge(ftHandle, FT_PURGE_RX | FT_PURGE_TX); // purge both buffers
   ftStatus |= FT_SetUSBParameters(ftHandle, imageSize, 0); // may improve speeds

   if (!strcmp(serial, "UM232H"))
   { 
      // sync fifo 
      ftStatus |= FT_SetBitMode(ftHandle, 0xFF, 0x40); // set all output
      cout << driverstr << "using sync fifo" << endl;
   }  
   else
   {   
      // async fifo
      ftStatus |= FT_SetBitMode(ftHandle, 0xFF, 0x0); // set all output, reset
      cout << driverstr << "using async fifo" << endl;
   }
   if (ftStatus != FT_OK) {
      FT_Close(ftHandle);
      cout << driverstr << "error connecting to device" << endl;
      return EXIT_FAILURE;
   }

   cout << driverstr << "connected to FTDI device: " << serial << endl;

   return EXIT_SUCCESS;
}

int Driver::disconnect() 
{
   FT_Close(ftHandle);
   cout << driverstr << "disconnected" << endl;
   return EXIT_SUCCESS;
}

int Driver::write(uint8_t data[], uint length)
{
	if (debug)
		cout << driverstr << "writing to FTDI device" << endl;

   ftStatus = FT_Write(ftHandle, (char*) data, length, &BytesWritten);
   if ((ftStatus != FT_OK) | (length != BytesWritten)) {
      FT_Close(ftHandle);
      cout << driverstr << "error writing to device" << endl;
      return EXIT_FAILURE;
   }

   if (debug)
      cout << driverstr << "wrote " << BytesWritten << " bytes" << endl;
   
   return EXIT_SUCCESS;
}

int Driver::captureRandom(Mat* img)
{
	srand(chrono::high_resolution_clock::now().time_since_epoch().count());
	for (int i = 0; i < 5120; i++)
	{
		bufferRx[i] = rand()%256;
	}
	img->data = bufferRx;
	return EXIT_SUCCESS;
}

int Driver::capture(uint8_t txBytes[], uint length, Mat* img, bool transformimg) 
{
	string capturestr = "capture: ";
	imgOK = false;
	ftStatus = FT_Write(ftHandle, txBytes, length, &BytesWritten);
	if (ftStatus != FT_OK) {
	   FT_Close(ftHandle);
	   cout << driverstr << capturestr << "error writing to device" << endl;
	   return EXIT_FAILURE;
	}
	
	if (debug)
		cout << driverstr << capturestr << "reading from FTDI device" << endl;
	
	ftStatus = FT_Read(ftHandle, bufferRx, imageSize, &BytesReceived);
	if (ftStatus != FT_OK) {
		FT_Close(ftHandle);
		cout << driverstr << capturestr << "error reading image" << endl;
		return EXIT_FAILURE;
	}
	else if (BytesReceived != imageSize) {
		if (debug)
			cout << driverstr << capturestr << "error detected" << endl;
		return EXIT_FAILURE;
	}
	else
	{
		if (debug)
			cout << driverstr << capturestr << "read " << BytesReceived << " bytes" << endl;
		imgOK = true;
	}
	
	if (imgOK)
	{
		if (transformimg)
			*img = Driver::fixImage(Mat(Size(80,64), CV_8UC1, bufferRx));
		else
			*img = Mat(Size(80,64), CV_8UC1, bufferRx);
	}
	if (debug)
		cout << driverstr << capturestr << "image complete" << endl;
	return EXIT_SUCCESS;
}

int Driver::captureData(uint8_t bytesImage[], uint lengthImage, uint8_t bytesData[], uint lengthData, Mat* img, Mat* data) 
{
	string capturestr = "capture data: ";

	ftStatus = FT_Write(ftHandle, bytesImage, lengthImage, &BytesWritten);
	if (ftStatus != FT_OK) {
		FT_Close(ftHandle);
		cout << driverstr << capturestr << "error writing to device" << endl;
		return EXIT_FAILURE;
	}
	
	ftStatus = FT_Read(ftHandle, bufferRx, imageSize, &BytesReceived);
	if (ftStatus != FT_OK) {
		FT_Close(ftHandle);
		cout << driverstr << capturestr << "error reading image" << endl;
		return EXIT_FAILURE;
	}
	//else if (BytesReceived != imageSize) {
	//	cout << driverstr << capturestr << "image error detected (" << BytesReceived << "/" << imageSize << " bytes)" << endl; 
	//	return EXIT_FAILURE;
	//}
	else
	{
		if (debug)
			cout << driverstr << capturestr << "read " << BytesReceived << " image bytes" << endl;
		*img = Driver::fixImage(Mat(Size(80,64), CV_8UC1, bufferRx));

		ftStatus = FT_Write(ftHandle, bytesData, lengthData, &BytesWritten);
		if (ftStatus != FT_OK) {
			FT_Close(ftHandle);
			cout << driverstr << capturestr << "error writing to device" << endl;
			return EXIT_FAILURE;
		}
	
		ftStatus = FT_Read(ftHandle, bufferRx, imageSize, &BytesReceived);
		if (ftStatus != FT_OK) {
			FT_Close(ftHandle);
			cout << driverstr << capturestr << "error reading data" << endl;
			return EXIT_FAILURE;
		}
		else if (BytesReceived != imageSize) {
			cout << driverstr << capturestr << "data error detected (" << BytesReceived << "/" << imageSize << " bytes)" << endl;
			return EXIT_FAILURE;
		}
		else
		{
			if (debug)
				cout << driverstr << capturestr << "read " << BytesReceived << " data bytes" << endl;
	
			*data = Driver::fixImage(Mat(Size(80,64), CV_8UC1, bufferRx));
	
			return EXIT_SUCCESS;
		}	
	}
}

int Driver::captureHistogram(uint8_t bytesImage[], uint lengthImage, uint8_t bytesData[], uint lengthData, Mat* img, Mat* datalsb, Mat* datamsb) 
{
	string capturestr = "capture histogram: ";

	ftStatus = FT_Write(ftHandle, bytesImage, lengthImage, &BytesWritten);
	if (ftStatus != FT_OK) {
		FT_Close(ftHandle);
		cout << driverstr << capturestr << "error writing to device" << endl;
		return EXIT_FAILURE;
	}
	
	ftStatus = FT_Read(ftHandle, bufferRx, imageSize, &BytesReceived);
	if (ftStatus != FT_OK) {
		FT_Close(ftHandle);
		cout << driverstr << capturestr << "error reading image" << endl;
		return EXIT_FAILURE;
	}
	else if (BytesReceived != imageSize) {
		cout << driverstr << capturestr << "image error detected (" << BytesReceived << "/" << imageSize << " bytes)" << endl; 
		return EXIT_FAILURE;
	}
	else
	{
		if (debug)
			cout << driverstr << capturestr << "read " << BytesReceived << " image bytes" << endl;
		*img = Driver::fixImage(Mat(Size(80,64), CV_8UC1, bufferRx));

		ftStatus = FT_Write(ftHandle, bytesData, lengthData, &BytesWritten);
		if (ftStatus != FT_OK) {
			FT_Close(ftHandle);
			cout << driverstr << capturestr << "error writing to device" << endl;
			return EXIT_FAILURE;
		}
	
		ftStatus = FT_Read(ftHandle, bufferRx, imageSize, &BytesReceived);
		if (ftStatus != FT_OK) {
			FT_Close(ftHandle);
			cout << driverstr << capturestr << "error reading data" << endl;
			return EXIT_FAILURE;
		}
		else if (BytesReceived != imageSize) {
			cout << driverstr << capturestr << "data error detected (" << BytesReceived << "/" << imageSize << " bytes)" << endl;
			return EXIT_FAILURE;
		}
		else
		{
			if (debug)
				cout << driverstr << capturestr << "read " << BytesReceived << " LSB bytes" << endl;
	
			*datalsb = Driver::fixImage(Mat(Size(80,64), CV_8UC1, bufferRx));
	
			ftStatus = FT_Read(ftHandle, bufferRx, imageSize, &BytesReceived);
			if (ftStatus != FT_OK) {
				FT_Close(ftHandle);
				cout << driverstr << capturestr << "error reading data" << endl;
				return EXIT_FAILURE;
			}
			else if (BytesReceived != imageSize) {
				cout << driverstr << capturestr << "data error detected (" << BytesReceived << "/" << imageSize << " bytes)" << endl;
				return EXIT_FAILURE;
			}
			else
			{
				if (debug)
					cout << driverstr << capturestr << "read " << BytesReceived << " MSB bytes" << endl;

				*datamsb = Driver::fixImage(Mat(Size(80,64), CV_8UC1, bufferRx));

				return EXIT_SUCCESS;
			}
		}	
	}
}

int Driver::repeatCommand(uint8_t bytesRepeat[], uint lengthRepeat)
{
	// this command will request a bulk repeat from the FPGA. data will be sent back using repeatData()
	
	string capturestr = "send repeat command: ";
	
	ftStatus = FT_Write(ftHandle, bytesRepeat, lengthRepeat, &BytesWritten);
	if (ftStatus != FT_OK) {
		FT_Close(ftHandle);
		cout << driverstr << capturestr << "error writing to device" << endl;
		return EXIT_FAILURE;
	}

	return EXIT_SUCCESS;
}

int Driver::repeatData(Mat* img)
{

	string capturestr = "receive repeat data: ";
	
	ftStatus = FT_Read(ftHandle, bufferRx, imageSize, &BytesReceived);
	
	if (ftStatus != FT_OK) {
		FT_Close(ftHandle);
		cout << driverstr << capturestr << "error reading image" << endl;
		return EXIT_FAILURE;
	}
	
	else if (BytesReceived != imageSize) {
		cout << driverstr << capturestr << "image error detected (" << BytesReceived << "/" << imageSize << " bytes)" << endl; 
		return EXIT_FAILURE;
	}
	
	else
	{
		if (debug)
			cout << driverstr << capturestr << "read " << BytesReceived << " image bytes" << endl;
		*img = Driver::fixImage(Mat(Size(80,64), CV_8UC1, bufferRx));
		
		return EXIT_SUCCESS;
	
	}
}

